CREATE TABLE `product_sku`
(
    `id`                    int(10) unsigned                        NOT NULL AUTO_INCREMENT COMMENT '商品SKU ID',
    `product_id`            int(10) unsigned                        NOT NULL COMMENT '商品ID',
    `attribute_symbol_list` varchar(255) COLLATE utf8mb4_unicode_ci          DEFAULT NULL COMMENT '商品属性值列表，关联属性表中的symbol，用逗号分隔，如:“1,3,4”',
