    `sell_price`            decimal(11, 2) unsigned                 NOT NULL COMMENT '售价',
    `cost_price`            decimal(11, 2) unsigned                          DEFAULT NULL COMMENT '成本价',
    `stock`                 int(10) unsigned                                 DEFAULT NULL COMMENT '库存数量',
    `stock_warn`            int(10)                                          DEFAULT NULL COMMENT '预警库存数量',
    `is_enable`             tinyint(1)                              NOT NULL DEFAULT '1' COMMENT '启用状态:1-启用,0-未启用',
    `create_user`           int(10) unsigned                        NOT NULL COMMENT '创建人id',
    `create_time`           datetime                                NOT NULL COMMENT '创建时间',
    `update_user`           int(10) unsigned                                 DEFAULT NULL COMMENT '更新人id',
    `update_time`           datetime                                         DEFAULT NULL COMMENT '更新时间',
    PRIMARY KEY (`id`)
) ENGINE = InnoDB
  AUTO_INCREMENT = 297
  DEFAULT CHARSET = utf8mb4
  COLLATE = utf8mb4_unicode_ci COMMENT ='商品sku';
