mapper.select:
SELECT count(*)
FROM user_waiter_history_log
WHERE store_id = #{storeId}
  AND waiter_id = #{waiterId}

SELECT count(*)
FROM user_waiter_history_log
WHERE store_id = #{storeId}
  AND waiter_id = #{waiterId}
  AND create_time >= DATE_FORMAT(NOW(), '%Y-%m-%d 00:00:00')
  AND create_time <= DATE_FORMAT(NOW(), '%Y-%m-%d 23:59:59')

SELECT count(*)
FROM (SELECT *
      FROM user_waiter_history_log
      WHERE store_id = #{storeId}
        AND waiter_id = #{waiterId}
        AND create_time > DATE_FORMAT(NOW(), '%Y-%m-%d 00:00:00')
        AND create_time <= DATE_FORMAT(NOW(), '%Y-%m-%d 23:59:59')
      GROUP BY user_id
     ) t

SELECT b.user_id,
       b.session_id
FROM user_waiter_history_log a
         INNER JOIN user_waiter b
WHERE a.user_id = b.user_id
  AND a.store_id = b.store_id
  AND a.waiter_id = #{waiterId}
  AND a.store_id = #{storeId}
  AND a.create_time >= #{startTime}
  AND b.create_time <= #{endTime}
GROUP BY b.user_id,
         b.session_id
